SELECT p.id AS id, p.mobile AS mobile, p.price AS price, p.start_point AS startPoint, p.start_point_longitude AS startPointLongitude, p.start_point_latitude AS startPointLatitude, p.destination AS destination, p.by_ways AS byWays, p.departure_time AS departureTime, p.passenger_num AS passengerNum, p.create_time AS createTime, p.update_time AS updateTime, p.car_brand AS carBrand, p.car_color AS carColor, p.car_type AS carType, p.plate_number_prefix AS plateNumberPrefix, p.plate_number AS plateNumber, u.nick_name AS userName, u.avatar AS avatar FROM carpool_publish AS p , carpool_user AS u WHERE p.publish_user_id = u.id AND p.data_status = 0 AND p.passenger_num > 0
AND p.`status` = #{status}
AND p.type = #{type}
AND p.user_type = #{userType}
AND p.departure_time >= #{departureTime}
AND p.start_point = #{startPoint}
AND p.destination = #{destination}
AND p.start_point_geo in
#{item}
AND p.destination_geo in
#{item}
ORDER BY p.departure_time ASC
